para.screen:
server {
  listen               80;
  root  /home/dhis/tomcat/webapps/ROOT; # Update path!
  client_max_body_size 10M;

  # Serve static content

  location ~ (\.js$|\.css$|\.gif$|\.woff$|\.ttf$|\.eot$|\.ico$|^/images/|^/icons/|^/dhis-web-commons/.*\.png$) {
    add_header  Cache-Control  public;
    expires  7d;
  } 

  # Proxy pass to servlet container

  location / {
    proxy_pass        http://localhost:8080/;
    proxy_redirect    off;
    proxy_set_header  Host               $host;
    proxy_set_header  X-Real-IP          $remote_addr;
    proxy_set_header  X-Forwarded-For    $proxy_add_x_forwarded_for;
    proxy_set_header  X-Forwarded-Proto  http;
  }
}

section.screen:
# HTTP server - rewrite to force use of SSL

server {
  listen     80;
  rewrite    ^ https://<server-url>$request_uri? permanent;
}

# HTTPS server

server {
  listen               443;
  root  /home/dhis/tomcat/webapps/ROOT; # Update path!
  client_max_body_size 10M;

  ssl                  on;
  ssl_certificate      server.crt;
  ssl_certificate_key  server.key;

  ssl_session_timeout  30m;

  ssl_protocols              SSLv2 SSLv3 TLSv1;
  ssl_ciphers                HIGH:!aNULL:!MD5;
  ssl_prefer_server_ciphers  on;

  # Serve static content

  location ~ (\.js$|\.css$|\.gif$|\.woff$|\.ttf$|\.eot$|\.ico$|^/images/|^/icons/|^/dhis-web-commons/.*\.png$) {
    add_header  Cache-Control  public;
    expires  7d;
  } 

  # Proxy pass to servlet container

  location / {
    proxy_pass        http://localhost:8080/;
    proxy_redirect    off;
    proxy_set_header  Host               $host;
    proxy_set_header  X-Real-IP          $remote_addr;
    proxy_set_header  X-Forwarded-For    $proxy_add_x_forwarded_for;
    proxy_set_header  X-Forwarded-Proto  https;
 }
}

http {
  # ...
  root              /home/dhis/tomcat/webapps/ROOT; # Update path!
  proxy_cache_path  /var/cache/nginx  keys_zone=dhis:250m  inactive=1d;
  gzip              on;

  # HTTP server - rewrite to force use of HTTPS

  server {
    listen     80;
    rewrite    ^ https://<server-ip>$request_uri? permanent;
  }

  # HTTPS server

  server {
    listen               443;
    client_max_body_size 10M;

    ssl                  on;
    ssl_certificate      server.crt;
    ssl_certificate_key  server.key;

    ssl_session_timeout  30m;

    ssl_protocols              SSLv2 SSLv3 TLSv1;
    ssl_ciphers                HIGH:!aNULL:!MD5;
    ssl_prefer_server_ciphers  on;

    # Serve static content

    location ~ (\.js$|\.css$|\.gif$|\.woff$|\.ttf$|\.eot$|\.ico$|^/images/|^/icons/|^/dhis-web-commons/.*\.png$) {
      add_header  Cache-Control  public;
      expires  7d;
    } 

    # Proxy pass to servlet container and potentially cache response

    location / {
      proxy_pass        http://localhost:8080/;
      proxy_redirect    off;
      proxy_set_header  Host               $host;
      proxy_set_header  X-Real-IP          $remote_addr;
      proxy_set_header  X-Forwarded-For    $proxy_add_x_forwarded_for;
      proxy_set_header  X-Forwarded-Proto  https;
      proxy_cache       dhis;
    }
  }
}

#!/bin/sh #Tomcat init script
HOME=/home/dhis/tomcat/bin
case $1 in start) sh ${HOME}/startup.sh ;; stop) sh ${HOME}/shutdown.sh ;; restart) sh ${HOME}/shutdown.sh sleep 5 sh ${HOME}/startup.sh ;; esac exit 0

server {
  listen       80;
  server_name  api.somedomain.com;
		  
  location ~ ^/(api/(charts|chartValues|reports|reportTables|documents|maps|organisationUnits)|dhis-web-commons/javascripts|images|dhis-web-commons-ajax-json|dhis-web-mapping|dhis-web-visualizer) {
    if ($request_method != GET) {
      return 405;
    }

    proxy_pass         http://localhost:8080;
    proxy_redirect     off;
    proxy_set_header   Host               $host;
    proxy_set_header   X-Real-IP          $remote_addr;
    proxy_set_header   X-Forwarded-For    $proxy_add_x_forwarded_for;
    proxy_set_header   X-Forwarded-Proto  http;
    proxy_set_header   Authorization      "Basic YWRtaW46ZGlzdHJpY3Q=";
    proxy_set_header   Cookie             "";
    proxy_hide_header  Set-Cookie;
  }
}
